import React, { useState, useEffect } from "react";
import TextInput from "./component/TextInput";
import TextArea from "./component/TextArea";
import Button from "./component/Button";

function PromptInput() {
  const [question, setQuestion] = useState("");
  const [conversation, setConversation] = useState("");
  const [ws, setWs] = useState(null);

  useEffect(() => {
    const socket = new WebSocket("ws://localhost:4000");
    setWs(socket);

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.response) {
        setConversation((prev) => `${prev}\nLlama: ${data.response}`);
      } else {
        console.error("Unexpected response format:", data);
      }
    };

    return () => {
      socket.close();
    };
  }, []);

  const handleInputChange = (e) => {
    setQuestion(e.target.value);
  };

  const handleSendMessage = () => {
    if (question.trim() === "" || !ws) return;

    setConversation((prev) => `${prev}\nUser: ${question}`);

    const message = {
      model: "llama3:8b",
      prompt: conversation + `\nUser: ${question}`,
      stream: false,
    };

    ws.send(JSON.stringify(message));
    setQuestion("");
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Alsurance - Own Your Prompts</h1>
      <TextArea
        value={conversation}
        placeholder="The AI response will appear here."
      />
      <TextInput
        value={question}
        onChange={handleInputChange}
        onEnter={handleSendMessage}
      />
      <Button onClick={handleSendMessage} />
    </div>
  );
}

export default PromptInput;
