import React, { useState, useEffect } from "react";
import TextInput from "./TextInput";
import TextArea from "./TextArea";
import Button from "./Button";

function PromptInput() {
  const [question, setQuestion] = useState("");
  const [conversation, setConversation] = useState("");
  const [ws, setWs] = useState(null);
  const [isTyping, setIsTyping] = useState(false); // Track if AI is typing
  const [aiResponse, setAiResponse] = useState(""); // Track AI response being typed
  const Spinner = () => <div className="spinner"></div>;

  useEffect(() => {
    const socket = new WebSocket("ws://localhost:4000");
    setWs(socket);

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.response) {
        displayResponseLetterByLetter(data.response);
      } else {
        console.error("Unexpected response format:", data);
      }
    };

    return () => {
      socket.close();
    };
  }, []);

  const handleInputChange = (e) => {
    setQuestion(e.target.value);
  };

  const handleSendMessage = () => {
    if (question.trim() === "" || !ws) return;

    // Append user input to the conversation history
    setConversation((prev) => `${prev}\nYou: ${question}`);
    setAiResponse(""); // Reset AI response being typed

    const message = {
      model: "llama3:8b",
      prompt: conversation + `\nYou: ${question}`,
      stream: false,
    };

    ws.send(JSON.stringify(message));
    setQuestion(""); // Clear input after sending
  };

  const displayResponseLetterByLetter = (response) => {
    setIsTyping(true); // Start typing effect
    let currentText = ""; // Initialize an empty string for the AI response being typed
    const delay = 20; // Delay in ms for each letter

    response.split("").forEach((char, index) => {
      setTimeout(() => {
        currentText += char;
        setAiResponse(currentText); // Update AI response progressively

        // Stop typing effect at the end of the response
        if (index === response.length - 1) {
          setIsTyping(false);
          // Append completed AI response to the conversation
          setConversation((prev) => `${prev}\nLlama: ${currentText}`);
          setAiResponse(""); // Clear aiResponse once it's added to conversation
        }
      }, index * delay);
    });
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Alsurance - Own Your Prompts</h1>
      <TextArea
        value={`${conversation}${isTyping ? `\nLlama: ${aiResponse}` : ""}`}
        placeholder="The AI response will appear here."
      />
      {isTyping && <Spinner />} {/* Show spinner while waiting for response */}
      <TextInput
        value={question}
        onChange={handleInputChange}
        onEnter={handleSendMessage}
      />
      <Button onClick={handleSendMessage} />
    </div>
  );
}

export default PromptInput;
