import React, { useState, useEffect } from "react";

function PromptInput() {
  const [question, setQuestion] = useState("");
  const [conversation, setConversation] = useState("");
  const [ws, setWs] = useState(null);

  useEffect(() => {
    // Initialize WebSocket connection
    const socket = new WebSocket("ws://localhost:4000");
    setWs(socket);

    // Handle incoming messages
    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.response) {
        setConversation((prev) => `${prev}\nLlama: ${data.response}`);
      } else {
        console.error("Unexpected response format:", data);
      }
    };

    // Clean up WebSocket connection on component unmount
    return () => {
      socket.close();
    };
  }, []);

  const handleInputChange = (e) => {
    setQuestion(e.target.value);
  };

  const handleSendMessage = () => {
    if (question.trim() === "" || !ws) return;

    // Add the user's question to the conversation history
    setConversation((prev) => `${prev}\nUser: ${question}`);

    // Prepare the message
    const message = {
      model: "llama3:8b",
      prompt: conversation + `\nUser: ${question}`,
      stream: false,
    };

    // Send the message to the WebSocket server
    ws.send(JSON.stringify(message));
    setQuestion(""); // Clear input field
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Alsurance - Own Your Prompts</h1>
      <textarea
        style={{ width: "80%", height: "200px", marginBottom: "10px" }}
        value={conversation}
        readOnly
      />
      <input
        type="text"
        value={question}
        onChange={handleInputChange}
        placeholder="Enter your question here"
        style={{
          width: "60%",
          padding: "10px",
          fontSize: "16px",
          marginBottom: "10px",
        }}
      />
      <button onClick={handleSendMessage} style={{ padding: "10px 20px" }}>
        Own Your Creation
      </button>
    </div>
  );
}

export default PromptInput;
